const buttonSubmit=document.querySelector(".button--reviews"),nameSubmit=document.getElementById("name"),surnameSubmit=document.getElementById("surname"),middleNameSubmit=document.getElementById("middle-name"),phoneSubmit=document.getElementById("phone"),emailSubmit=document.getElementById("email"),popapError=document.querySelector(".popap__error"),buttonError=popapError.querySelector(".button--error"),popapSent=document.querySelector(".popap__sent"),buttonSent=popapSent.querySelector(".button--sent"),textSent=document.querySelector(".description__subtitle--popap-sent"),requiredInput=document.querySelectorAll(".required");buttonSubmit.onclick=e=>{for(e.preventDefault(),i=0;i<requiredInput.length;i++)""==requiredInput[i].value||""==nameSubmit.value||""==surnameSubmit.value||""==middleNameSubmit.value||""==phoneSubmit.value||""==emailSubmit.value?(popapError.classList.add("popap__block-err"),requiredInput[i].classList.add("required-err")):(requiredInput[i].classList.remove("required-err"),popapSent.classList.add("popap__block-sent"),textSent.textContent=`${surnameSubmit.value} ${nameSubmit.value} ${middleNameSubmit.value} Спасибо за ваше участие, ваш отзыв уже поступил к нам.  В ближайшее время мы опубликуем его на сайте.\n        После публикации вы получите уведомление на вашу  электронную почту ${emailSubmit.value}`)},buttonError.onclick=()=>{popapError.classList.remove("popap__block-err")},buttonSent.onclick=()=>{popapSent.classList.remove("popap__block-sent")};